import React from 'react';
var moment = require('moment');

const DayCard = ({ reading, degreeType }) => {
    let newDate = new Date();
    const weekday = reading.dt * 1000;
    newDate.setTime(weekday);

    const fahrenheit = Math.round(reading.main.temp);
    const celsius = Math.round((fahrenheit - 32) * 5 / 9);

    const imgURL = `owf owf-${reading.weather[0].id} owf-5x`;

    return (
        <div className="col-sm-2">
            <div className="card" style={{ backgroundColor: 'lightyellow' }}>
                <h3 className="card-title">{moment(newDate).format('dddd')}</h3>
                <p className="text-muted">{moment(newDate).format('MMMM Do, h:mm a')}</p>
                <i className={imgURL}></i>
                <h2 style={{ fontWeight: 'bolder' }}>{degreeType === "celsius" ? celsius + "째C" : fahrenheit + "째F"}</h2>
                <div className="card-body">
                    <p className="card-text">{reading.weather[0].description}</p>
                </div>
            </div>
        </div >
    )
}

export default DayCard;
