import React from 'react';
import { Card } from 'react-bootstrap';
import CitySelector from 'Api';

const WeatherCard = ({ dt, temp_min, temp_max, main, icon, city }) => {
    // create a date object with Date class constructor
    const date = new Date(dt);
    return (
        <Card style={{
            width: '12rem',
            backgroundColor: 'lightblue',
        }}>
            <p> {city}</p>
            <Container className="App">
                <CitySelector onSearch={(city) => setUrl(`${API_BASE_URL}/data/2.5/forecast?q=${city}&cnt=5&appid=${API_KEY}`)} />

                {/* conditionally render  */}
                {data && <WeatherList weathers={data.list} />}
            </Container>
            <Card.Img
                variant="top"
                // get the src from example url and pass the icon prop for icon code
                src={`http://openweathermap.org/img/wn/${icon}@2x.png`}
            />
            <Card.Body>
                <Card.Title>{main}</Card.Title>
                {/*  datetime is received in milliseconds, let's turn into local date time */}
                <p>
                    {date.toLocaleDateString()} - {date.toLocaleTimeString()}
                </p>
                {/* minimum temperature */}
                <p>Min: {temp_min}</p>
                {/* maximum temperature */}
                <p>Max: {temp_max}</p>
            </Card.Body>
        </Card>
    );
};

export default WeatherCard;
