// import logo from './logo.svg';
import React, { Component } from 'react';
import './App.css';

class App extends React.Component {
  state = {
    latitude: null,
    longitude: null,
    temp: null,
    locationName: null
  }

  componentDidMount() {
    if (navigator.geolocation) {
      this.getPostions()
        .then((position) => {
          console.log(position.coords.latitude);
          // this.setState({ latitude: position.coords.latitude })
          this.getWeather(position.coords.latitude, position.coords.longitude)
        })
    }
  }

  getPostions = () => {
    return new Promise(function (resolve, reject) {
      navigator.geolocation.getCurrentPosition(resolve, reject);
    });
  };

  getWeather = async (latitude, longitude) => {
    const api = await fetch(
      `https://api.openweathermap.org/data/2.5/weather?lat=${latitude}&lon=${longitude}&units=metric&appid=38ff886a4e3b7104f324a6067130ef6a`
    );
    const data = await api.json();
    console.log(data);
    this.setState(
      {
        temp: Math.round(data.main.temp),
        locationName: data.name
      }
    )
  }
  render() {
    return (
      <React.Fragment>
        <div className="col-8 d-flex justify-content-center">
          <div className="col-6 app-bg d-flex flex-wrap">
            <div className="col-12">
              <h2 className="text-white m-0">Delhi</h2>
              <p className="text-white">IN</p>
            </div>
            <div className="col-12 mt-auto d-flex">
              <div>
                <h2 className="text-white">20:00</h2>
                <p className="text-white">Sunday, January 31, 2021</p>
              </div>
              <div className="ml-auto">
                <h1 className="text-white">16Â°C</h1>
              </div>
            </div>
          </div>
        </div>
      </React.Fragment>
    );
  }
}

export default App;
