// import logo from './logo.svg';
import React, { Component } from 'react';
import './App.css';
import Clock from 'react-live-clock';
import WeatherInfo from './weatherinfo';
import { getSunrise, getSunset } from 'sunrise-sunset-js';

const sunset = getSunset(51.4541, -2.5920);
const sunrise = getSunrise(51.1788, -1.8262, new Date("2000-06-21"));

navigator.geolocation.getCurrentPosition(function (position) {
  console.log(getSunset(position.coords.latitude, position.coords.longitude));
});

function getSolarEventsForYear(latitude, longitude, year) {
  const result = [];
  const start = new Date(year, 0, 1).getTime();
  for (let i = 0; i < 366; i++) {
    const d = new Date(start + (i * 24 * 60 * 60 * 1000));
    if (d.getFullYear() > year) break; // For non-leap year
    result.push(['sunrise', getSunrise(latitude, longitude, d)]);
    result.push(['sunset', getSunset(latitude, longitude, d)]);
  }
  return result.filter(event => Boolean(event[1])).sort((a, b) => a - b);
}
getSolarEventsForYear(51.1788, -1.8262, 2019);


class App extends React.Component {
  state = {
    latitude: null,
    longitude: null,
    temp: null,
    locationName: null,
    country: null,
    humidity: null,
    visibility: null,
    windSpeed: null,
    feelsLike: null,
    minTemp: null,
    maxTemp: null,
    weatherName: null,
    sunrise: null,
    sunset: null
  }

  componentDidMount() {
    if (navigator.geolocation) {
      this.getPostions()
        .then((position) => {
          console.log(position.coords.latitude);
          // this.setState({ latitude: position.coords.latitude })
          this.getWeather(position.coords.latitude, position.coords.longitude)
        })
    }
  }

  getPostions = () => {
    return new Promise(function (resolve, reject) {
      navigator.geolocation.getCurrentPosition(resolve, reject);
    });
  };

  getWeather = async (latitude, longitude) => {
    const api = await fetch(
      `https://api.openweathermap.org/data/2.5/weather?lat=${latitude}&lon=${longitude}&units=metric&appid=38ff886a4e3b7104f324a6067130ef6a`
    );
    const data = await api.json();
    console.log(data);
    this.setState(
      {
        temp: Math.round(data.main.temp),
        locationName: data.name,
        country: data.sys.country,
        humidity: data.main.humidity,
        visibility: data.visibility,
        windSpeed: data.wind.speed,
        feelsLike: Math.round(data.main.feels_like),
        minTemp: Math.round(data.main.temp_min),
        maxTemp: Math.round(data.main.temp_max),
        weatherName: data.weather[0].main,
        sunrise: data.sys.sunrise,
        sunset: data.sys.sunset
      }
    )
  }
  render() {
    return (
      <React.Fragment>
        <div className="col-8 d-flex justify-content-center py-5">
          <div className="col-6 app-bg d-flex flex-wrap py-3">
            <div className="col-12">
              <h2 className="text-white m-0">{(this.state.locationName)}</h2>
              <p className="text-white">{(this.state.country)}</p>
            </div>
            <div className="col-12 mt-auto d-flex">
              <div className="my-auto">
                <h2 className="text-white m-0"><Clock format={'HH:mm:ss'} ticking={true} /></h2>
                <p className="text-white m-0">
                  <Clock date={''} format={'dddd, MMMM DD, YYYY'} />
                </p>
              </div>
              <div className="ml-auto">
                <h1 className="text-white">{(this.state.temp)}Â°C</h1>
              </div>
            </div>
          </div>
          <WeatherInfo humidity={this.state.humidity} visibility={this.state.visibility} windSpeed={this.state.windSpeed} feelsLike={this.state.feelsLike} minTemp={this.state.minTemp} maxTemp={this.state.maxTemp} weatherName={this.state.weatherName} sunrise={this.state.sunrise} sunset={this.state.sunset} />
        </div>
      </React.Fragment>
    );
  }
}

export default App;
